import logo from './logo.svg';
import './App.css';
import web3 from './web3';
import lottery from './lottery';
import React, { Component } from 'react'

class App extends Component {

  state = {
    manger:'',
    players: [],
    balance: '',
    value: '',
    message:''
  };

// refreshManager = async () => { 


// }

async componentDidMount() {
 const manager  = await lottery.methods.manager().call();
 const players = await lottery.methods.getPlayers().call();
 const balance = await web3.eth.getBalance(lottery.options.address);
 const metaMaskAccounts = await web3.eth.getAccounts();
 this.setState({manager, players, balance, metaMaskAccounts});
}

distribute = () => {
const firstPrice = 4/9 * this.state.balance;
const secondPrice = 3/9 * this.state.balance;
const thirdPrice = 2/9 * this.state.balance;
console.log(firstPrice,secondPrice,thirdPrice);
}

async shouldComponentUpdate(nextProps,nextState) {
  console.log(this.state.result);
  if (this.state.result) {

    this.setState({result: false});
    const manager  = await lottery.methods.manager().call();
    const players = await lottery.methods.getPlayers().call();
    const balance = await web3.eth.getBalance(lottery.options.address);
    this.setState({manager, players, balance });
  }
  return true;
}

onSubmit = async (event) => {
  event.preventDefault();
  const accounts = await web3.eth.getAccounts();
  this.setState({message:'Waiting on transaction success...'});
  await lottery.methods.enter().send({

    from: accounts[0],
    value: web3.utils.toWei(this.state.value,'ether')

  });
  this.setState({result: true});
  this.setState({value:''});
  this.setState({message:'Congrats you have been entered!'});

}


onClick = async (event) => {
console.log(await lottery.methods.getWinners().call());

  // const accounts = await web3.eth.getAccounts();
  // this.setState({message:'Waiting on transaction success...'});
  // await lottery.methods.pickWinner().send({
  //   from:accounts[0]
  // });
  // const winners = await lottery.methods.getWinners();
  // console.log("respomse");
  // console.log(winners);
  // this.setState({message:'Winner has been picked'});
}


render() {
  //web3.eth.getAccounts().then(console.log);
  let playerList = ''
  this.state.players.map((item,position) => {
    return playerList+= item + (position<this.state.players.length-1 ? ', ' : '');
  })
  return (
   <div> 
   <h2> Lottery Contract </h2>
   <p>This contract is managed by {this.state.manager}
   <br/>There are currently { this.state.players.length } people entered,
   <br/> with address {playerList}
   <br/>competing to win {web3.utils.fromWei(this.state.balance,'ether') } Ether! </p>

   <hr/> 

   <form onSubmit={this.onSubmit}>

   <h4>Want to try your luck?</h4>
   <div>
   <label> Amount of ether to enter </label>
   <input 
   value = {this.state.value}
   onChange={event=> this.setState({value: event.target.value})}

   />
   <button>Enter</button>
   </div>
   </form>
   <hr/>
   {this.state.metaMaskAccounts && this.state.manager === this.state.metaMaskAccounts[0] &&  
    <>

    <h4>Ready to pick winner?</h4>
    <button onClick={this.onClick}>Pick a winner</button>

    </>}
    <h1>{this.state.message}</h1>
    </div>


    );

  }

}

export default App;
